        id
        name
        email
        lineItems(first:5){
          edges{
            node{
              name
              image {
                id
                    originalSrc
                    altText
              }
               
            }
          }
        }
      }
    }
  }
}`;


function DataTableExample(data) {
  const items = data.orders.edges;


  function itemsToNode({cursor, node}) {
    return {node}.node
  };

  function nodeToRows(node){
    console.log("node")
    console.log(node)
    node.forEach((element, index)=> {
      console.log(`Current index: ${index}`);
      console.log(console.log(Object.values(element)))
    });
  }

  const prerow = nodeToRows(items.map(itemsToNode))
  //const rows = items.map(itemsToNode)
  console.log('prerow');
  console.log(prerow);


  return (
    <Page title="Sales by product">
      <Card>
        <DataTable
          columnContentTypes={[
            'numeric',
            'text',
            'text',
            'text',
          ]}
          headings={[
            'id',
            'name',
            'email',
            'order',
          ]}
          rows={rows}
          //totals={['', '', '', 255, '$155,830.00']}
        />
      </Card>
    </Page>
  );
}

