                          : ''
                      }
                    />
                  );
                  const price = item.node.netPaymentSet.shopMoney.amount;
                  return (
                    
                    <ResourceList.Item
                    media = {orderName}
                     // id={item.id}
                      media={media}
                      //accessibilityLabel={`View details for ${item.title}`}
                      onClick={() => {
                        //store.set('item', item);
                        console.log('store.set')
                        //redirectToProduct();
                      }}
                    >
                      <Stack>
                      <Stack.Item>
                          <p>{orderName}</p>
                        </Stack.Item>
                        <Stack.Item fill>
                          <h3>
                            <TextStyle variation="strong">

                              {item.node.id}
                            </TextStyle>
                          </h3>
                        </Stack.Item>
                        <Stack.Item>
                          <p>${price}</p>
                        </Stack.Item>
                        <Stack.Item>
                          <p>Expires on R </p>
                        </Stack.Item>
                      </Stack>
                    </ResourceList.Item>
                  );
                }}
              />
            </Card>
          );
        }}
      </Query>
    );
  }
}*/

